import React, { useState, useContext } from "react";
import "./css/achievements.css";
import Popup from "./PopupInterface.tsx";
// @ts-ignore
import "./css/languages.css";
// @ts-ignore
import "./css/root.css";
import fce from "./assets/documents/FCE.pdf";

export default function Languages(){
    //FCE popup
    const [isFCEPopupOpen, setIsFCEPopupOpen] = useState(false);

    const openFCEPopup = () => {
        setIsFCEPopupOpen(true);
    }

    const closeFCEPopup = () => {
        setIsFCEPopupOpen(false);
    }

    const FCEPopupTitle = "First Certificate Exam Results";

    const FCEPopupContent = <div>
        <embed src={fce} type="application/pdf" width="100%" height="400px"scrollbar="1" />
    </div>

    //TOEFL popup
    const [isTOEFLPopupOpen, setIsTOEFLPopupOpen] = useState(false);

    const openTOEFLPopup = () => {
        setIsTOEFLPopupOpen(true);
    }

    const closeTOEFLPopup = () => {
        setIsTOEFLPopupOpen(false);
    }

    const TOEFLPopupTitle = "TOEFL Results";

    const TOEFLPopupContent = <div>
        
    </div>

    //DELF B2 result
    const [isDELFPopupOpen, setIsDELFPopupOpen] = useState(false);

    const openDELFPopup = () => {
        setIsDELFPopupOpen(true);
    }

    const closeDELFPopup = () => {
        setIsDELFPopupOpen(false);
    }

    const DELFPopupTitle = "DELF B2 Results";

    const DELFPopupContent = <div>

    </div>

    return <>
        <Popup isOpen={isFCEPopupOpen} onRequestClose={closeFCEPopup} title={FCEPopupTitle} content={FCEPopupContent} />
        <Popup isOpen={isTOEFLPopupOpen} onRequestClose={closeTOEFLPopup} title={TOEFLPopupTitle} content={TOEFLPopupContent} />
        <Popup isOpen={isDELFPopupOpen} onRequestClose={closeDELFPopup} title={DELFPopupTitle} content={DELFPopupContent} />

        <h1>Languages</h1>

        <div className="languages_container">
            <div className="catalan">
                <h2>Catalan</h2>
                <p>It is my native language and when I lived in Barcelona I used to speak it most of the time.</p>
            </div>
            <div className="spanish">
                <h2>Spanish</h2>
                <p>It is also a native language as a spanish citizen, so I am fluent and I also use it all the time.</p>
            </div>
            <div className="english">
                <h2>English</h2>
                <p>I started to study it when I was 3, I passed the FCE in 2021 and obtained a 93/120 in the TOEFL in 2021.</p>
                <p className="read_more_inline" onClick={openFCEPopup}>FCE results</p>
                <p className="read_more_inline" onClick={openTOEFLPopup}>TOEFL results</p>
            </div>
            <div className="french">
                <h2>French</h2>
                <p>I started to study it when I was 11, and in 2021 I passed the DELF B2 exam</p>
                <p className="read_more_inline" onClick={openDELFPopup}>DELF B2 results</p>
            </div>
        </div>
    </>
}
